/**
 * Generate a voucher code, that depends on a given string input.
 *
 * @param $input
 *
 * @return false|string
 */
function voucherCodeHash($input) {
  return substr(hash('sha512', $input . VOUCHER_IMPORT_HASH_SALT), 0, 20);
}

